import React, { Component } from 'react';
import {Link} from 'react-router-dom';

export default class News extends Component {
    constructor(props) {
        super(props);
        this.state = {
            slug: this.props.match.params.slug
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(){
        //let temp = this.props.match.params.slug
       this.setState({
           slug: this.props.match.params.slug
       })
       //console.log(temp);
    }

    render() {
        console.log(this.state.slug);
        return (
            <>
                <section className="news">
                    <div className="news__title">
                        <h1 className="news__title-name">
                            News
                        </h1>
                    </div>
                    <div className="news__center">

                    </div>
                </section>
                <section className="list__btn">
                    <ul className="list">
                        <li><Link to ={'/news/page/1'} className="list__btn-page" onClick = {(e)=>this.handleClick} >1</Link></li>
                        <li><Link to ={'/news/page/2'} className="list__btn-page" onClick = {this.handleClick} >2</Link></li>
                        <li><Link to ={`/news/page/${3}`} className="list__btn-page" onClick = {this.handleClick} >3</Link></li>
                        <li><Link to ={`/news/page/${4}`} className="list__btn-page" onClick = {this.handleClick} >4</Link></li>
                        <li><Link to ={`/news/page/${1}`} className="list__btn-page" onClick = {this.handleClick} ><i className="fas fa-arrow-right"></i></Link></li>
                    </ul>
                </section>
            </>
        )
    }
}
