import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import { NewsContext } from '../context';

export default class News extends Component {
    constructor(props) {
        super(props);
        this.number = 1;
        this.handleClick = this.handleClick.bind(this);
    }

    static contextType = NewsContext;

    handleClick(e,id){
       this.number = id;
    }

    render() {

        let news = this.context.news;
        console.log();

        return (
            <>
                <section className="news">
                    <div className="news__title">
                        <h1 className="news__title-name">
                            News
                        </h1>
                    </div>
                    <div className="news__center">

                    </div>
                </section>
                <section className="list__btn">
                    <ul className="list">
                        <li>
                            <Link to ={`/news/page/${1}`} 
                            className="list__btn-page" 
                            onClick = {(e)=>this.handleClick(e,1)} >
                                1
                            </Link>
                        </li>
                        <li>
                            <Link to ={`/news/page/${2}`} 
                            className="list__btn-page" 
                            onClick = {(e)=>this.handleClick(e,2)} >
                                2
                            </Link></li>
                        <li>
                            <Link to ={`/news/page/${3}`} 
                            className="list__btn-page" 
                            onClick = {(e)=>this.handleClick(e,3)} >
                                3
                            </Link>
                        </li>
                        <li>
                            <Link to ={`/news/page/${4}`} 
                            className="list__btn-page" 
                            onClick = {(e)=>this.handleClick(e,4)}>
                                4
                            </Link>
                        </li>
                    </ul>
                </section>
            </>
        )
    }
}
