import React, { Component } from 'react';
import logo from '../images/logo.png';
import { Link } from 'react-router-dom';

export default class NavBar extends Component {

    state = {
        isOpen: false
    }

    handleToggle() {
        this.setState({
            isOpen: !this.state.isOpen
        });
    }

    render() {
        return (
            <nav className="nav">
                <div className="nav__header">
                    <Link to='/' >
                        <img src={logo} alt = "family-logo" className="nav__header-logo" />
                    </Link>
                    <span className="nav__header-btn" onClick = {this.handleToggle} >
                        <i className="fas fa-bars"/>
                    </span>
                </div>
                <ul className="nav__links">
                    <li><a href="" className="nav__single-link">Home</a></li>
                    <li><a href="all-news.html" className="nav__single-link">News</a></li>
                    <li><a href="contact.html" className="nav__single-link">Contact</a></li>
                </ul>
            </nav>
        )
    }
}
